import { CHANGE_VIEW, CHANGE_BACKGROUND_COLOUR, CHANGE_FONT_COLOUR } from '../actions/types';


const initialState = {
    view: "dashboard",
    fontColour: 'white',
    backgroundColour: '',
    loading: true
}

export default function(state = initialState, action) {
    switch(action.type) {
        case CHANGE_VIEW:
            return {
                ...state,
                view: action.payload,
                loading: false
            }
        default:
            return state;
    }
}
