import '../App.css';
import React from 'react';
import tabsolute from '../images/tabsolutelogo2.png';
import WidgetCustomization from './WidgetCustomization';

class SettingsBar extends React.Component {
    render() {
        return(
            <div>
                {this.props.settingsVisible === "true" ? 
                    <div style={{display: 'flex', height: '100vh', margin: '0', padding: '0', position: 'fixed', overflowX: 'hidden', zIndex: '100'}}>
                        <div style={{height: '100%', width: '300px', backgroundColor: '#202124', borderRadius: '0px 10px 10px 0px', textAlign: 'center', padding: '5px'}}>
                            <img src={tabsolute} alt="tabsolute_logo" width="85px" height="60px" style={{marginTop: '20px'}} />
                            <h4 style={{color: '#4495F6'}}>TABSOLUTE</h4>
                            <div style={{padding: '10px', display: "flex", textAlign: 'left', margin: '20px 0px 10px 0px', backgroundColor: '#30363D', borderRadius: '7px'}}>
                                <p style={{color: 'white', fontWeight: 'bolder'}}>Add Widget:</p>
                                <form onSubmit={this.props.addNewWidget} style={{marginLeft: '20px'}}>
                                    <select name="widgets" id="widgets">
                                        <option selected disabled>Select Widget</option>
                                        <option value="Greeting">Greeting</option>
                                        <option value="Weather">Weather</option>
                                        <option value="Quote">Quote</option>
                                        <option value="Scores">Scores</option>
                                        <option value="Time">Time</option>
                                    </select>
                                    <input type="submit" value="+" style={{backgroundColor: 'white', border: 'none', borderRadius: '10%', fontSize: '15px', margin: '0px 0px 0px 30px'}}></input>
                                </form>
                            </div>
                            {this.props.widgets.map((widget)  => {
                                if (widget.type === "Weather") {
                                    return(
                                        <div style={{backgroundColor: '#30363D', padding: '10px', marginBottom: '10px', borderRadius: '7px'}}>
                                            <div style={{textAlign: 'left', margin: '0px 10px 10px 0px'}}>
                                                <p style={{float: 'left', color: 'white', fontWeight: 'bolder'}}>{widget.type}</p>
                                                <div style={{float: 'right'}}>
                                                    <i onClick={this.props.toggleDisplaySettings} id={widget.id} style={{color: 'white', cursor: 'pointer', margin: '0px 5px 0px 5px'}} class="fas fa-wrench"></i>
                                                    <i style={{color: 'white', cursor: 'pointer', margin: '0px 5px 0px 5px'}} class="fas fa-trash-alt" onClick={this.props.handleDelete} id={widget.id}></i>
                                                </div>
                                            </div>
                                            <input id={widget.id} type="text" placeholder="City" defaultValue={widget.value.split(":@:")[0]} onKeyPress={this.props.handleCityChange} style={{marginTop: '10px'}}></input>
                                            {widget.display.customizationVisible === "true" ? <WidgetCustomization handleFontChange={this.props.handleFontChange} widget={widget} /> : null}
                                        </div>
                                    )
                                }
                                else if (widget.type === "Time") {
                                    return(
                                        <div style={{backgroundColor: '#30363D', padding: '10px', marginBottom: '10px', borderRadius: '7px'}}>
                                            <div style={{textAlign: 'left', margin: '0px 10px 10px 0px'}}>
                                                <p style={{float: 'left', color: 'white', fontWeight: 'bolder'}}>{widget.type}</p>
                                                <div style={{float: 'right'}}>
                                                    <i onClick={this.props.toggleDisplaySettings} id={widget.id} style={{color: 'white', cursor: 'pointer', margin: '0px 5px 0px 5px'}} class="fas fa-wrench"></i>
                                                    <i style={{color: 'white', cursor: 'pointer', margin: '0px 5px 0px 5px'}} class="fas fa-trash-alt" onClick={this.props.handleDelete} id={widget.id}></i>
                                                </div>
                                            </div>
                                            <fieldset id="clocktype" style={{border: "none", display: 'flex', color: 'white'}}>
                                                <label>12-hour</label>
                                                <input style={{marginTop: '10px'}} type="radio" value="12" onChange={this.props.handleClockChange} id={widget.id} name="clocktype" checked={widget.value === "12"}></input>
                                                <label>24-hour</label>
                                                <input style={{marginTop: '10px'}} type="radio" value="24" onChange={this.props.handleClockChange} id={widget.id} name="clocktype" checked={widget.value === "24"}></input>
                                            </fieldset>
                                            {widget.display.customizationVisible === "true" ? <WidgetCustomization handleFontChange={this.props.handleFontChange} widget={widget} /> : null}
                                        </div>
                                    )
                                }
                                else if (widget.type === "Quote") {
                                    return (
                                        <div style={{backgroundColor: '#30363D', padding: '10px', marginBottom: '10px', borderRadius: '7px', minHeight: '30px'}}>
                                            <div style={{textAlign: 'left', margin: '0px 10px 20px 0px'}}>
                                                <p style={{float: 'left', color: 'white', fontWeight: 'bolder'}}>{widget.type}</p>
                                                <div style={{float: 'right'}}>
                                                    <i onClick={this.props.toggleDisplaySettings} id={widget.id} style={{color: 'white', cursor: 'pointer', margin: '0px 5px 0px 5px'}} class="fas fa-wrench"></i>
                                                    <i style={{color: 'white', cursor: 'pointer', margin: '0px 5px 0px 5px'}} class="fas fa-trash-alt" onClick={this.props.handleDelete} id={widget.id}></i>
                                                </div>
                                            </div>
                                            {widget.display.customizationVisible === "true" ? <WidgetCustomization handleFontChange={this.props.handleFontChange} widget={widget} /> : null}
                                        </div>
                                    )
                                }
                                else {
                                    return(
                                        <div style={{backgroundColor: '#30363D', padding: '10px', marginBottom: '10px', borderRadius: '7px'}}>
                                            <div style={{textAlign: 'left', margin: '0px 10px 10px 0px'}}>
                                                <p style={{float: 'left', color: 'white', fontWeight: 'bolder'}}>{widget.type}</p>
                                                <div style={{float: 'right'}}>
                                                    <i onClick={this.props.toggleDisplaySettings} id={widget.id} style={{color: 'white', cursor: 'pointer', margin: '0px 5px 0px 5px'}} class="fas fa-wrench"></i>
                                                    <i style={{color: 'white', cursor: 'pointer', margin: '0px 5px 0px 5px'}} class="fas fa-trash-alt" onClick={this.props.handleDelete} id={widget.id}></i>
                                                </div>
                                            </div>
                                            <input style={{marginTop: '10px'}} type="text" defaultValue={widget.value} onChange={this.props.handleNameChange} id={widget.id}></input>
                                            {widget.display.customizationVisible === "true" ? <WidgetCustomization handleFontChange={this.props.handleFontChange} widget={widget} /> : null}
                                        </div>
                                    )
                                }
                            })}
                            <p><a href="https://paypal.me/jordengerovac?locale.x=en_US" style={{textDecoration: 'none', color: '#4495F6'}}>Support the cause</a></p>
                        </div>
                        <div className="clickAway" style={{width: '80vw', height: '100%'}} onClick={this.props.toggleSettings}></div>
                    </div>
                    : null }
            </div>
        )
    }
}

export default SettingsBar;
