import './App.css';
import React from 'react';
import SettingsBar from './components/SettingsBar';
import WeatherWidget from './components/widgets/WeatherWidget';
import { v4 as uuid } from 'uuid';
import Switch from '@material-ui/core/Switch';
import { MuiThemeProvider, createMuiTheme } from '@material-ui/core/styles';
import { Provider } from 'react-redux';
import store from './store';
import { connect } from 'react-redux';
import { fetchPhoto } from './actions/photoActions';

class App extends React.Component {
  constructor() {
    super();

    this.state = {      
      photo: "",
      photoDetails: {
        artist: "",
        profileLink: "",
        photoLink: ""
      },
      photoBackground: "true",
      refresh: "false",
      settingsVisible: "false",
      viewType: "dashboard",
      tileColumns: 4,
      widgets: [
        {
          id: uuid(),
          type: "Greeting",
          value: "",
          display: {
            customizationVisible: "false",
            font: "50",
            location: "center"
          }
        },
        {
          id: uuid(),
          type: "Time",
          value: "12",
          display: {
            customizationVisible: "false",
            font: "50",
            location: "center"
          },
        }
      ],
      linkWidget: {
        value: "12",
        numLinks: 3,
        display: {
          customizationVisible: "false",
          font: "50",
          location: "center"
        },
      },
      tiles: [
        {
          id: uuid(),
          name: "Twitter",
          value: "https://www.twitter.com",
          icon: "fab fa-twitter"
        },
        {
          id: uuid(),
          name: "Facebook",
          value: "https://www.facebook.com",
          icon: "fab fa-facebook"
        },
        {
          id: uuid(),
          name: "YouTube",
          value: "https://www.youtube.com",
          icon: "fab fa-youtube"
        }
      ]
    };
  }

  toggleView = () => {
    var toggle = "";
    if (this.state.viewType === "dashboard") {
      toggle = "tile";
    }
    else {
      toggle = "dashboard";
    }
    this.setState({
      viewType: toggle
    }, () => localStorage.setItem("state", JSON.stringify(this.state)))
  }

  togglePhotoBackground = () => {
    var toggle = "";
    if (this.state.photoBackground === "true") {
      toggle = "false";
    }
    else {
      toggle = "true";
    }
    this.setState({
      photoBackground: toggle
    }, () => localStorage.setItem("state", JSON.stringify(this.state)))
  }

  toggleSettings = () => {
    var toggle = "";
    if (this.state.settingsVisible === "true") {
      toggle = "false";
    }
    else {
      toggle = "true";
    }
    this.setState({
      settingsVisible: toggle
    }, () => localStorage.setItem("state", JSON.stringify(this.state)));
  }

  toggleDisplaySettings = (e) => {
    if (e.target.id === "linkWidget") {
      const newLinkWidget = this.state.linkWidget;
      if (newLinkWidget.display.customizationVisible === "true") {
        newLinkWidget.display.customizationVisible = "false"
      }
      else {
        newLinkWidget.display.customizationVisible = "true"
      }
      this.setState({
        linkWidget: newLinkWidget
      }, () => localStorage.setItem("state", JSON.stringify(this.state)))
    }
    else {
      this.setState({
        widgets: this.state.widgets.map((widget) => {
          if (widget.id === e.target.id) {
            if (widget.display.customizationVisible === "false")
              widget.display.customizationVisible = "true";
            else
              widget.display.customizationVisible = "false";
          }
          return widget;
        })
      }, () => localStorage.setItem("state", JSON.stringify(this.state)));
    }
  }

  handleFontChange = (e) => {
    if (e.key === "Enter") {
      this.setState({
        widgets: this.state.widgets.map((widget) => {
          if (widget.id === e.target.id) {
            widget.display.font = e.target.value
          }
          return widget;
        })
      }, () => localStorage.setItem("state", JSON.stringify(this.state)))
    }
  }

  handleTileChange = (e) => {
    const attribute = e.target.name;
    this.setState({
      tiles: this.state.tiles.map((tile) => {
        if (tile.id === e.target.id) {
          if (attribute === "name") {
            tile.name = e.target.value
          }
          else if (attribute === "icon") {
            tile.icon = e.target.value
          }
          else {
            tile.value = e.target.value
          }
        }
        return tile;
      })
    }, () => localStorage.setItem("state", JSON.stringify(this.state)));
  }

  handleClockChange = (e) => {
    this.setState({
      widgets: this.state.widgets.map((widget) => {
        if (widget.id === e.target.id) {
          widget.value = e.target.value
        }
        return widget;
      })
    }, () => localStorage.setItem("state", JSON.stringify(this.state)))
  }

  handleNameChange = (e) => {
    this.setState({
      widgets: this.state.widgets.map((widget) => {
        if (widget.id === e.target.id) {
          widget.value = e.target.value
        }
        return widget;
      })
    }, () => localStorage.setItem("state", JSON.stringify(this.state)))
  }

  handleDelete = (e) => {
    const newWidgetsState = this.state.widgets.filter(widget => e.target.id !== widget.id);
    this.setState({
      widgets: newWidgetsState
    }, () => localStorage.setItem("state", JSON.stringify(this.state)));
  }

  handleTileDelete = (e) => {
    const newTilesState = this.state.tiles.filter(tile => e.target.id !== tile.id);
    this.setState({
      tiles: newTilesState
    }, () => localStorage.setItem("state", JSON.stringify(this.state)));
  }

  addNewWidget = (e) => {
    e.preventDefault();
    if (e.target[0].value !== "Select Widget") {
      const newWidget = {
        id: uuid(),
        type: e.target[0].value,
        value: "",
        display: {
          customizationVisible: "false",
          font: "20",
          location: "center"
        }
      }

      if (e.target[0].value === "Quote") {
        this.getRandomQuote(newWidget.id);
      }
      else if (e.target[0].value === "Scores") {
        this.getScores(newWidget.id);
      }

      this.setState({
        widgets: [...this.state.widgets, newWidget]
      }, () => localStorage.setItem("state", JSON.stringify(this.state)))
    }
  }

  addNewLinkTile = (e) => {
    console.log('here')
    e.preventDefault();
    const newLinkTile = {
      id: uuid(),
      name: "New Link Tile",
      value: "https://www.google.com",
      icon: "fas fa-link"
    }

    this.setState({
      tiles: [...this.state.tiles, newLinkTile]
    }, () => localStorage.setItem("state", JSON.stringify(this.state)))
  }

  getRandomQuote = (wid) => {
    fetch('https://type.fit/api/quotes')
      .then(res => res.json())
      .then(
      (result) => {
        var random_num = Math.floor(Math.random() * result.length);
        this.setState({
          widgets: this.state.widgets.map((widget) => {
            if (widget.id === wid) {
              widget.value = result[random_num].text + ":@:" + result[random_num].author
            }
            return widget;
          })
        }, () => localStorage.setItem("state", JSON.stringify(this.state)))
      }
    )
  }

  getWeatherStatus = (city, wid) => {
    const WEATHER_API_KEY = `${process.env.REACT_APP_WEATHER_API_KEY}`
    fetch('https://api.openweathermap.org/data/2.5/weather?q=' + city + '&units=metric&appid=' + WEATHER_API_KEY)
      .then(res => res.json())
      .then(
      (result) => {
        if (result.hasOwnProperty("main")) {
          this.setState({
            temp_min: result.main.temp_min,
            temp_max: result.main.temp_max,
            clouds: result.weather[0].description
          }, () => localStorage.setItem("state", JSON.stringify(this.state)));

          this.setState({
            widgets: this.state.widgets.map((widget) => {
              if (widget.id === wid) {
                widget.value = city + ":@:" + result.main.temp_min + ":@:" + result.main.temp_max + ":@:" + result.weather[0].description
              }
              return widget;
            })
          }, () => localStorage.setItem("state", JSON.stringify(this.state)))
        }
      },
      (error) => {
          console.log("error")
      }
    )
}

  handleCityChange = (e) => {
    if (e.key === "Enter") {
      this.setState({
        widgets: this.state.widgets.map((widget) => {
          if (widget.id === e.target.id) {
            widget.value = e.target.value
          }
          return widget;
        })
      }, () => localStorage.setItem("state", JSON.stringify(this.state)));
      this.getWeatherStatus(e.target.value, e.target.id);
    }
  }

  tick = () => {
    var current_time = new Date();
    this.setState({
      time: current_time
    });

    if (current_time.getMinutes() % 5 === 0 && current_time.getSeconds() === 0) {
      this.setState({
        refresh: "true"
      }, () => localStorage.setItem("state", JSON.stringify(this.state)));
    }
  }


  componentDidMount() {
    const UNSPLASH_API_KEY = `${process.env.REACT_APP_UNSPLASH_API_KEY}`;
    var getState = JSON.parse(localStorage.getItem("state"));
    if (getState === null) {
      localStorage.setItem("state", JSON.stringify(this.state));
      getState = JSON.parse(localStorage.getItem("state"));
    }
    this.setState(getState);
    this.setState({
      settingsVisible: "false"
    });

    // GET photo
    fetchPhoto();

    //if (getState.photo === "" || getState.refresh === "true") {
      /*
      fetch("https://api.unsplash.com/collections/4578671/photos/?orientation=landscape&per_page=100&client_id=" + UNSPLASH_API_KEY)
