              }
            }, () => localStorage.setItem("state", JSON.stringify(this.state)))
          },
          // Note: it's important to handle errors here
          // instead of a catch() block so that we don't swallow
          // exceptions from actual bugs in components.
          (error) => {
            console.log("error")
          }
        )
        */
    //}

    // GET weather data
    // this.getWeatherStatus(this.state.city);

    // GET random quote and weather data
    getState.widgets.map((widget) => {
      if (widget.type === "Quote")
        this.getRandomQuote(widget.id);
      else if (widget.type === "Weather")
        this.getWeatherStatus(widget.value, widget.id);
      else if (widget.type === "Scores")
        this.getScores(widget.id);
    })

    setInterval(() => {
      this.tick();
    }, 1000);
  }

  render(){
    var photoURL;
    if (this.state.photoBackground === "true") {
      photoURL = this.state.photo;
    }
    else {
      photoURL = ""
    }
