import './App.css';
import React from 'react';
import SettingsBar from './components/SettingsBar';
import WeatherStatus from './components/WeatherStatus';
import { v4 as uuid } from 'uuid';

class App extends React.Component {
  constructor() {
    super();
    this.state = {      
      count: 0,
      photo: "",
      time: new Date(),
      refresh: "false",
      settingsVisible: "false",
      city: "toronto",
      name: "",
      feelsLike: "",
      temp: "",
      clouds: "",
      quoteText: "",
      quoteAuthor: "",
      widgets: [
        {
          id: "1",
          type: "Greeting",
          value: "", 
        },
        {
          id: "2",
          type: "Time",
          value: "",
        },
        {
          id: "3",
          type: "Quote",
          value: ""
        }
      ]
    };
  }

  toggleSettings = () => {
    var toggle = ""
    if (this.state.settingsVisible === "true") {
      toggle = "false";
    }
    else {
      toggle = "true";
    }
    this.setState({
      settingsVisible: toggle
    }, () => localStorage.setItem("state", JSON.stringify(this.state)))
  }

  handleClockChange = () => {
    console.log(e.target.value);
    this.setState({
      widgets: this.state.widgets.map((widget) => {
        if (widget.id === e.target.id) {
          widget.value = e.target.value
        }
        return widget;
      })
    }, () => localStorage.setItem("state", JSON.stringify(this.state)))
  }

  handleNameChange = (e) => {
    this.setState({
      widgets: this.state.widgets.map((widget) => {
        if (widget.id === e.target.id) {
          widget.value = e.target.value
        }
        return widget;
      })
    }, () => localStorage.setItem("state", JSON.stringify(this.state)))
  }

  handleDelete = (e) => {
    const newWidgetsState = this.state.widgets.filter(widget => e.target.id !== widget.id);
    this.setState({
      widgets: newWidgetsState
    }, () => localStorage.setItem("state", JSON.stringify(this.state)));
  }

  addNewWidget = (e) => {
    e.preventDefault();
    const newWidget = {
      id: uuid(),
      type: e.target[0].value,
      value: ""
    }
    this.setState({
      widgets: [...this.state.widgets, newWidget]
    }, () => localStorage.setItem("state", JSON.stringify(this.state)))
    console.log(this.state.widgets)
  }

  getRandomQuote = () => {
    fetch('https://type.fit/api/quotes')
      .then(res => res.json())
      .then(
      (result) => {
        var random_num = Math.floor(Math.random() * result.length);
        this.setState({
          quoteText: result[random_num].text,
          quoteAuthor: result[random_num].author
        })
      }
    )
  }

  getWeatherStatus = (city, id) => {
    const WEATHER_API_KEY = `${process.env.REACT_APP_WEATHER_API_KEY}`
    fetch('https://api.openweathermap.org/data/2.5/weather?q=' + city + '&units=metric&appid=' + WEATHER_API_KEY)
      .then(res => res.json())
      .then(
      (result) => {
        if (result.hasOwnProperty("main")) {
          this.setState({
            temp: result.main.temp,
            feelsLike: result.main.feels_like,
            clouds: result.weather[0].description
          }, () => localStorage.setItem("state", JSON.stringify(this.state)));
        }
      },
      (error) => {
          console.log("error")
      }
    )
}

  handleCityChange = (e) => {
    if (e.key === "Enter") {
      this.setState({
        widgets: this.state.widgets.map((widget) => {
          if (widget.id === e.target.id) {
            widget.value = e.target.value
          }
          return widget;
        })
      }, () => localStorage.setItem("state", JSON.stringify(this.state)));
      this.getWeatherStatus(e.target.value, e.target.id);
    }
  }

  tick = () => {
    var current_time = new Date();
    this.setState({
      time: current_time
    });

    if (current_time.getMinutes() % 5 === 0 && current_time.getSeconds() === 0) {
      this.setState({
        refresh: "true"
      }, () => localStorage.setItem("state", JSON.stringify(this.state)));
    }
  }


  componentDidMount() {
    const UNSPLASH_API_KEY = `${process.env.REACT_APP_UNSPLASH_API_KEY}`;
    /*
    const parsedCount = Number(localStorage.getItem("count") || 0);
    const getPhoto = String(localStorage.getItem("photo") || "");
    const getRefresh = String(localStorage.getItem("refresh") || "false");
    const getName = String(localStorage.getItem("name") || "");
    */

    var getState = JSON.parse(localStorage.getItem("state"));
    if (getState === null) {
      localStorage.setItem("state", JSON.stringify(this.state));
      getState = JSON.parse(localStorage.getItem("state"));
    }
    this.setState(getState);

    // GET photo
    if (getState.photo === "" || getState.refresh === "true") {
      fetch("https://api.unsplash.com/collections/764604/photos/?orientation=landscape&per_page=100&client_id=" + UNSPLASH_API_KEY)
        .then(res => res.json())
        .then(
          (result) => {
            var random_num = Math.floor(Math.random() * result.length);
            this.setState({
              photo: result[random_num].urls.regular,
              refresh: "false"
            }, () => localStorage.setItem("state", JSON.stringify(this.state)))
            //localStorage.setItem("photo", result[random_num].urls.regular);
            //localStorage.setItem("refresh", "false");
          },
          // Note: it's important to handle errors here
          // instead of a catch() block so that we don't swallow
          // exceptions from actual bugs in components.
          (error) => {
            console.log("error")
          }
        )
    }

    // GET weather data
    // this.getWeatherStatus(this.state.city);

    // GET random quote
    this.getRandomQuote();

    setInterval(() => {
      this.tick();
    }, 1000);
  }

  render(){
    const current_time = new Date();
    var greeting = "";
    var ampm = current_time.getHours() >= 12 ? 'PM' : 'AM';
    if (current_time.getHours() >= 21) {
      greeting = "night";
    }
    else if (current_time.getHours() >= 18) {
      greeting = "evening";
    }
    else if (current_time.getHours() >= 12) {
      greeting = "afternoon"
    }
    else {
      greeting = "morning"
    }

    return (
      <div style={{backgroundImage: 'url(' + this.state.photo + ')', width: '100vw', height: '100vh', backgroundSize: 'cover', margin: '0', padding: '0', backgroundRepeat: 'no-repeat'}}>
        <SettingsBar settingsVisible={this.state.settingsVisible} toggleSettings={this.toggleSettings} handleNameChange={this.handleNameChange} name={this.state.name} city={this.state.city} handleCityChange={this.handleCityChange} addNewWidget={this.addNewWidget} widgets={this.state.widgets} handleDelete={this.handleDelete} handleClockChange={this.handleClockChange} />
        <div className="settings">
          <i style={{color: 'white', fontSize: '22px', margin: '7px'}} className="fas fa-cog" onClick={this.toggleSettings}></i>
          <div style={{float:'right'}}>
            {this.state.widgets.map((widget) => {
              if (widget.type === "Weather") 
                return (<WeatherStatus city={widget.value} feelsLike={this.state.feelsLike} temp={this.state.temp} clouds={this.state.clouds} />)
            })}
          </div>
        </div>
          <div className="centered" style={{textAlign: 'center'}}>
          {this.state.widgets.map((widget) => {
            if (widget.type === "Greeting") {
              if (widget.value !== "") 
                return (<h3 style={{margin: '0', color: 'white'}}>Good {greeting}, {widget.value}</h3>)

              else 
                return (<h3 style={{margin: '0', color: 'white'}}>Good {greeting}</h3>)

            }
            else if (widget.type === "Quote") {
              return (<div><h4 style={{color: 'white'}}>{this.state.quoteText}</h4><h4 style={{color: 'white'}}>- {this.state.quoteAuthor}</h4></div>)
             }
            else if (widget.type === "Time") {
              return (<h1 style={{margin: '0', color: 'white'}}>{current_time.toLocaleTimeString().split(":")[0]}:{current_time.toLocaleTimeString().split(":")[1]} {ampm}</h1>)
             }
            }
          )}
          </div>
      </div>
    );
  }
}

export default App;
