import '../App.css';
import SettingsBar from './SettingsBar';
import WeatherWidget from './widgets/WeatherWidget';
import Switch from '@material-ui/core/Switch';
import { MuiThemeProvider, createMuiTheme } from '@material-ui/core/styles';
import React from 'react';
import { connect } from 'react-redux';
import { fetchPhoto } from '../actions/photoActions';

class DashboardView extends React.Component {
    constructor() {
        super();
        this.state = ({
            photoBackground: "true"
        })
    }

    componentDidMount() {
        this.props.fetchPhoto()
    }

    togglePhotoBackground = () => {
        var toggle = "";
        if (this.state.photoBackground === "true") {
          toggle = "false";
        }
        else {
          toggle = "true";
        }
        this.setState({
          photoBackground: toggle
        }, () => localStorage.setItem("state", JSON.stringify(this.state)))
      }

    render() {
        const photoObject = this.state.photo;
        var photoURL = "";
        if (this.state.photoBackground === "true") {
            photoURL = photoObject.urls.regular;
        }
        var artist = photoObject.user.first_name + " " + photoObject.user.last_name;
        var photoLink = photoObject.user.links.html;
        var profileLink = photoObject.links.html;

        const current_time = new Date();
        var greeting = "";
        var ampm = current_time.getHours() >= 12 ? 'PM' : 'AM';
        if (current_time.getHours() >= 21) {
        greeting = "night";
        }
        else if (current_time.getHours() >= 18) {
        greeting = "evening";
        }
        else if (current_time.getHours() >= 12) {
        greeting = "afternoon"
        }
        else {
        greeting = "morning"
        }

        const theme = createMuiTheme({
        palette: {
            primary: {
            main: '#4495F6'
            }
        }
        });
        return (
            <div className="dashboard" style={{backgroundImage: 'url(' + photoURL + ')', width: '100vw', height: '100vh', backgroundSize: 'cover', margin: '0', padding: '0', backgroundRepeat: 'no-repeat', backgroundColor: '#202124'}}>

                <div style={{position: 'absolute', bottom: '0px'}}>
                    <MuiThemeProvider theme={theme}>
                    <Switch
                        checked={this.state.photoBackground === "true"}
                        onClick={this.togglePhotoBackground}
                        color="primary"
                        name="photoBackground"
                        inputProps={{ 'aria-label': 'primary checkbox' }}
                    />
                    </MuiThemeProvider>
                </div>

                {this.state.photoBackground === "true" ? <div style={{position: 'absolute', bottom: '5px', right: '5px'}}>
                    <p><a href={photoLink + "https://unsplash.com/?utm_source=tabsolute&utm_medium=referral"}>Photo</a> by <a href={profileLink + "/?utm_source=tabsolute&utm_medium=referral"}>{artist}</a> on <a href="https://unsplash.com/?utm_source=tabsolute&utm_medium=referral">Unsplash</a></p>
                </div> : null}

            </div>
        )
    }
}

const mapStateToProps = state => ({
    photo: state.photo.item
})

export default connect(mapStateToProps, { fetchPhoto })(DashboardView);
