import '../App.css';
import React from 'react';
import tabsolute from '../images/tabsolutelogo2.png';
import WidgetCustomization from './WidgetCustomization';

class SettingsBar extends React.Component {
    render() {
        return(
            <div>
                {this.props.settingsVisible === "true" ? 
                    <div style={{display: 'flex', height: '100vh', margin: '0', padding: '0', position: 'fixed', overflowX: 'hidden', zIndex: '100'}}>
                        <div style={{height: '100%', width: '300px', backgroundColor: '#202124', borderRadius: '0px 10px 10px 0px', textAlign: 'center', padding: '5px'}}>
                            <img src={tabsolute} alt="tabsolute_logo" width="85px" height="60px" style={{marginTop: '20px'}} />
                            <h4 style={{color: '#4495F6'}}>TABSOLUTE</h4>
                            <div style={{padding: '10px', display: "flex", textAlign: 'left', margin: '20px 0px 10px 0px', backgroundColor: '#30363D', borderRadius: '7px'}}>
                                <p style={{color: 'white', fontWeight: 'bolder'}}>Add Widget:</p>
                                <form onSubmit={this.props.addNewWidget}>
                                    <select name="widgets" id="widgets">
                                        <option selected disabled>Select Widget</option>
                                        <option value="Greeting">Greeting</option>
                                        <option value="Weather">Weather</option>
                                        <option value="Quote">Quote</option>
                                        <option value="Scores">Scores</option>
                                        <option value="Time">Time</option>
                                    </select>
                                    <input type="submit" value="Add"></input>
                                </form>
                            </div>
                            {this.props.widgets.map((widget) => {
                                if (widget.type == "Weather") {
                                    return(
                                        <div style={{backgroundColor: '#30363D', padding: '10px', marginBottom: '10px', borderRadius: '7px'}}>
                                            <div style={{textAlign: 'left', margin: '0px 10px 10px 0px'}}>
                                                <p style={{float: 'left', color: 'white', fontWeight: 'bolder'}}>{widget.type}</p>
                                                <button onClick={this.props.handleDelete} id={widget.id} style={{float: 'right'}}>delete</button>
                                            </div>
                                            <input id={widget.id} type="text" placeholder="City" defaultValue={widget.value} onKeyPress={this.props.handleCityChange} style={{marginTop: '10px'}}></input>
                                        </div>
                                    )
                                }
                                else if (widget.type == "Time") {
                                    return(
                                        <div style={{backgroundColor: '#30363D', padding: '10px', marginBottom: '10px', borderRadius: '7px'}}>
                                            <div style={{textAlign: 'left', margin: '0px 10px 10px 0px'}}>
                                                <p style={{float: 'left', color: 'white', fontWeight: 'bolder'}}>{widget.type}</p>
                                                <button onClick={this.props.handleDelete} id={widget.id} style={{float: 'right'}}>delete</button>
                                            </div>
                                            <fieldset id="clocktype" style={{border: "none", display: 'flex'}}>
                                                <label>12-hour</label>
                                                <input style={{marginTop: '10px'}} type="radio" defaultValue={widget.value} onChange={this.props.handleNameChange} id={widget.id} name="clocktype"></input>
                                                <label>24-hour</label>
                                                <input style={{marginTop: '10px'}} type="radio" defaultValue={widget.value} onChange={this.props.handleNameChange} id={widget.id} name="clocktype"></input>
                                            </fieldset>
                                        </div>
                                    )
                                }
                                else {
                                    return(
                                        <div style={{backgroundColor: '#30363D', padding: '10px', marginBottom: '10px', borderRadius: '7px'}}>
                                            <div style={{textAlign: 'left', margin: '0px 10px 10px 0px'}}>
                                                <p style={{float: 'left', color: 'white', fontWeight: 'bolder'}}>{widget.type}</p>
                                                <button onClick={this.props.handleDelete} id={widget.id} style={{float: 'right'}}>delete</button>
                                            </div>
                                            <input style={{marginTop: '10px'}} type="text" defaultValue={widget.value} onChange={this.props.handleNameChange} id={widget.id}></input>
                                        </div>
                                    )
                                }
                            })}
                        </div> 
                        <div className="clickAway" style={{width: '80vw', height: '100%'}} onClick={this.props.toggleSettings}></div>
                    </div>
                    : null }
            </div>
        )
    }
}

export default SettingsBar;
