export const saveState = (state) => {
    try {
      const serializedState = JSON.stringify(state);
      localStorage.setItem('photo', serializedState);
    } catch (e) {
      // Ignore write errors;
    }
};

export const loadState = () => {
    try {
      const serializedState = localStorage.getItem('photo');
      if(serializedState === null) {
        return undefined;
      }
      return JSON.parse(serializedState);
    } catch (e) {
      return undefined;
    }
};
