import logo from './logo.svg';
import './App.css';
import React, { useState } from 'react';

class App extends React.Component {
  constructor() {
    super();
    this.state = {      
      count: 0,
      photo: "" ,
      time: new Date().toLocaleTimeString()
    };
  }

  tick = () => {
    this.setState({
      time: new Date().toLocaleTimeString()
    });
    console.log('tick')
  }

  componentDidMount() {
    const API_KEY = `${process.env.REACT_APP_UNSPLASH_API_KEY}`;
    fetch("https://api.unsplash.com/photos/random/?client_id= " + API_KEY)
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({photo: result.urls.raw});
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          console.log("error")
        }
      )
      const interval = setInterval(() => {
        this.tick();
      }, 1000);
  }

  render(){
    return (
      <div style={{backgroundImage: 'url(' + this.state.photo + ')', width: '100vw', height: '100vh', backgroundSize: '100% 100%', margin: '0', padding: '0', backgroundRepeat: 'no-repeat'}}>
          <div className="centered">
            <h2 style={{margin: '0', color: 'black'}}>{this.state.time}</h2>
          </div>
      </div>
    );
  }
}

export default App;
