import logo from './logo.svg';
import './App.css';
import React, { useState, useEffect } from 'react';

class App extends React.Component {
  constructor() {
    super();
    this.state = {      
      count: 0,
      photo: "",
      time: new Date(),
      photoLoaded: false
    };
  }

  tick = () => {
    this.setState({
      time: new Date()
    });
  }

  counter = () => {
      const parsedCount = Number(localStorage.getItem("count") || 0);
      this.setState({count: parsedCount + 1});
      localStorage.setItem("count", this.state.count);
  }

  componentDidMount() {
    const API_KEY = `${process.env.REACT_APP_UNSPLASH_API_KEY}`;
    if (this.state.photoLoaded) {
      fetch("https://api.unsplash.com/collections/764604/photos/?orientation=landscape&per_page=100&client_id=" + API_KEY)
        .then(res => res.json())
        .then(
          (result) => {
            var random_num = Math.floor(Math.random() * result.length);
            this.setState({
              photo: result[random_num].urls.regular,
              photoLoaded: true
            });
            console.log(result);
          },
          // Note: it's important to handle errors here
          // instead of a catch() block so that we don't swallow
          // exceptions from actual bugs in components.
          (error) => {
            console.log("error")
          }
        )
    }
      const interval = setInterval(() => {
        this.tick();
      }, 1000);
  }

  render(){
    localStorage.setItem('photoLoaded', true);
    var greeting = "";
    var ampm = this.state.time.getHours() >= 12 ? 'PM' : 'AM';
    if (this.state.time.getHours() > 8) {
      greeting = "evening";
    }
    else {
      greeting = "morning"
    }

    return (
      <div style={{backgroundImage: 'url(' + this.state.photo + ')', width: '100vw', height: '100vh', backgroundSize: '100% 100%', margin: '0', padding: '0', backgroundRepeat: 'no-repeat'}}>
          <div className="centered" style={{textAlign: 'center'}}>
            <h3 style={{margin: '0', color: 'white'}}>Good {greeting}, Jorden</h3>
            <h1 style={{margin: '0', color: 'white'}}>{this.state.time.getHours()}:{this.state.time.getMinutes()} {ampm}</h1>
            <p>You clicked {this.state.count} times</p>
            <button onClick={this.counter()}>Click</button>
          </div>
      </div>
    );
  }
}

export default App;
