import '../App.css';
import React from 'react';

class LinkCustomization extends React.Component {
    render() {
        const iconMap = [
            {
                key: "Twitter",
                value: "fab fa-twitter"
            },
            {
                key: "Facebook",
                value: "fab fa-facebook"
            },
            {
                key: "YouTube",
                value: "fab fa-youtube"
            }
        ];

        return(
            <div style={{textAlign: 'left'}}>
                <div style={{backgroundColor: '#30363D', padding: '10px', borderRadius: '7px'}}>
                    <div style={{textAlign: 'left', margin: '0px 10px 20px 0px', display: 'flex'}}>
                        <p style={{float: 'left', color: 'white', marginTop: '3px'}}>Add Link Tile</p>
                        <button style={{backgroundColor: '#0bb508', border: 'none', borderRadius: '10%', fontSize: '15px', margin: '0px 0px 0px 18px', color: 'white', height: '25px', width: '25px', paddingBottom: '2px', cursor: 'pointer'}} onClick={this.props.addNewLinkTile}>+</button>
                    </div>
                    {this.props.tiles.map((tile) => {
                        return(
                            <div style={{marginBottom: '15px', display: 'flex', color: 'white'}}>
                                <div>
                                    <label>Name</label>
                                    <input value={tile.name} name="name" onChange={this.props.handleTileChange} id={tile.id} style={{width: '140px', marginLeft: '20px'}}></input>
                                    <label>URL</label>
                                    <input value={tile.value} name="value" onChange={this.props.handleTileChange} id={tile.id} style={{width: '140px', marginLeft: '31px'}}></input>
                                    <label>Icon</label>
                                    <select id="tileIcons" style={{border: 'none', height: '20px', width: '147px', margin: '3px 0px 0px 34px'}}>
                                            <option selected disabled>Select Icon</option>
                                            {iconMap.map((icon) => {

                                                    console.log(icon)

                                            })}
                                        </select>
                                </div>
                                <div>
                                    <i style={{color: 'white', cursor: 'pointer', margin: '0px 0px 0px 5px'}} class="fas fa-trash-alt" onClick={this.props.handleTileDelete} id={tile.id}></i>
                                </div>
                            </div>
                        )
                    })}
                </div>
            </div>
        )
    }
}

export default LinkCustomization;
