import '../App.css';
import React from 'react';

class SearchBar extends React.Component {
    constructor() {
        super();
        this.state = {
            searchText: ''
        }
    }
    handleChange = (event) => {
        this.setState({
            searchText: event.target.value
        })
    }

    render() {
        return (
            <div>
                <p style={{fontSize: this.props.widget.display.font + 'px'}}>aasdfsa</p>
                <input style={{marginTop: '10px', border: 'none', width: ('50' * this.props.widget.display.font) + 'px', fontSize: this.props.widget.display.font + 'px', height: this.props.widget.display.font + 'px'}} type="text" value={this.props.widget.value} onChange={this.handleChange} id={this.props.widget.id} value={this.state.searchText}></input>
            </div>
        )
    }
}

export default SearchBar;
