import '../App.css';
import React, { useState, useEffect } from 'react';

class SettingsBar extends React.Component {
    constructor(props) {
        super(props);
    }

    render() {
        return(
            <div>
                {this.props.settingsVisible ? 
                    <div style={{display: 'flex', height: '100vh', margin: '0', padding: '0', position: 'fixed'}}>
                        <div style={{height: '100%', width: '20vw',backgroundColor: 'white', border: '3px solid #4495F6', borderRadius: '0px 10px 10px 0px'}}>Settings Bar</div> 
                        <div className="clickAway" style={{opacity: '1', width: '80vw', height: '100%'}} onClick={this.props.toggleSettings}></div>
                    </div>
                    : null }
            </div>
        )
    }
}

export default SettingsBar;
