import { ADD_WIDGET, DELETE_WIDGET } from '../actions/types';


const initialState = {
    widgets: [
        {
          id: uuid(),
          type: "Greeting",
          value: "",
          display: {
            customizationVisible: "false",
            font: "50",
            location: "center"
          }
        },
        {
          id: uuid(),
          type: "Time",
          value: "12",
          display: {
            customizationVisible: "false",
            font: "50",
            location: "center"
          },
        }
    ]
};

export default function(state = initialState, action) {
    switch(action.type) {
        case ADD_WIDGET:
            return {
                ...state,
                view: action.payload,
                loading: false
            }
        default:
            return state;
    }
}
