import logo from './logo.svg';
import './App.css';
import React, { useState, useEffect } from 'react';
import SettingsBar from './components/SettingsBar';

class App extends React.Component {
  constructor() {
    super();
    this.state = {      
      count: 0,
      photo: "",
      time: new Date(),
      refresh: "false",
      settingsVisible: false,
      name: ""
    };
  }

  toggleSettings = () => {
    this.setState({
      settingsVisible: !this.state.settingsVisible
    })
  }

  handleChange = (event) => {
    this.setState({
      name: event.target.value
    });
    localStorage.setItem("name", event.target.value);
  }

  tick = () => {
    this.setState({
      time: new Date()
    });
    if (this.state.time.getMinutes() % 5 === 0 && this.state.time.getSeconds() === 0) {
      localStorage.setItem("refresh", "true");
    }
  }

  counter = () => {
      //const parsedCount = Number(localStorage.getItem("count") || 0);
      this.setState({count: this.state.count + 1});
      localStorage.setItem("count", this.state.count + 1);
  }

  componentDidMount() {
    const API_KEY = `${process.env.REACT_APP_UNSPLASH_API_KEY}`;
    const parsedCount = Number(localStorage.getItem("count") || 0);
    const getPhoto = String(localStorage.getItem("photo") || "");
    const getRefresh = String(localStorage.getItem("refresh") || "false");
    const getName = String(localStorage.getItem("name") || "");

    this.setState({
      count: parsedCount,
      photo: getPhoto,
      name: getName
    })
    if (getPhoto === "" || getRefresh === "true") {
      fetch("https://api.unsplash.com/collections/764604/photos/?orientation=landscape&per_page=100&client_id=" + API_KEY)
        .then(res => res.json())
        .then(
          (result) => {
            var random_num = Math.floor(Math.random() * result.length);
            this.setState({
              photo: result[random_num].urls.regular
            });
            localStorage.setItem("photo", result[random_num].urls.regular);
            localStorage.setItem("refresh", "false");
          },
          // Note: it's important to handle errors here
          // instead of a catch() block so that we don't swallow
          // exceptions from actual bugs in components.
          (error) => {
            console.log("error")
          }
        )
    }
      const interval = setInterval(() => {
        this.tick();
      }, 1000);
  }

  render(){
    var greeting = "";
    var ampm = this.state.time.getHours() >= 12 ? 'PM' : 'AM';
    if (this.state.time.getHours() >= 21) {
      greeting = "night";
    }
    else if (this.state.time.getHours() >= 18) {
      greeting = "evening";
    }
    else if (this.state.time.getHours() >= 12) {
      greeting = "afternoon"
    }
    else {
      greeting = "morning"
    }

    return (
      <div style={{backgroundImage: 'url(' + this.state.photo + ')', width: '100vw', height: '100vh', backgroundSize: 'cover', margin: '0', padding: '0', backgroundRepeat: 'no-repeat'}}>
        <SettingsBar settingsVisible={this.state.settingsVisible} toggleSettings={this.toggleSettings} handleChange={this.handleChange} name={this.state.name}/>
        <div className="settings">
          <i style={{color: 'white', fontSize: '22px', margin: '7px'}} class="fas fa-cog" onClick={this.toggleSettings}></i>
        </div>
          <div className="centered" style={{textAlign: 'center'}}>
            <h3 style={{margin: '0', color: 'white'}}>Good {greeting}, {this.state.name}</h3>
            <h1 style={{margin: '0', color: 'white'}}>{this.state.time.toLocaleTimeString().split(":")[0]}:{this.state.time.toLocaleTimeString().split(":")[1]} {ampm}</h1>
          </div>
      </div>
    );
  }
}

export default App;
