import '../App.css';
import SettingsBar from './SettingsBar';
import WeatherWidget from './widgets/WeatherWidget';
import Switch from '@material-ui/core/Switch';
import { MuiThemeProvider, createMuiTheme } from '@material-ui/core/styles';
import React from 'react';
import { connect } from 'react-redux';
import { fetchPhoto } from '../actions/photoActions';
import { loadState, saveState } from '../actions/stateActions';
import store from '../store';

class DashboardView extends React.Component {
    constructor(props) {
        super(props);
        this.state = ({
            photoBackground: "true",
            photo: ""
        })
    }

    componentDidMount() {
      if (!store.getState().photoDetails) {
        this.props.fetchPhoto();
      }
    }

    togglePhotoBackground = () => {
        var toggle = "";
        if (this.state.photoBackground === "true") {
          toggle = "false";
        }
        else {
          toggle = "true";
        }
        this.setState({
          photoBackground: toggle
        }, () => localStorage.setItem("state", JSON.stringify(this.state)))
      }

    render() {
      const theme = createMuiTheme({
        palette: {
          primary: {
            main: '#4495F6'
          }
        }
      });
      console.log(this.props.photo);
        return (
            <div className="dashboard" style={{backgroundImage: 'url(' + String(this.props.photoDetails.photo.urls.regular) + ')', width: '100vw', height: '100vh', backgroundSize: 'cover', margin: '0', padding: '0', backgroundRepeat: 'no-repeat', backgroundColor: '#202124'}}>
              <div style={{position: 'absolute', bottom: '0px'}}>
                <MuiThemeProvider theme={theme}>
                  <Switch
                    checked={this.state.photoBackground === "true"}
                    onClick={this.togglePhotoBackground}
                    color="primary"
                    name="photoBackground"
                    inputProps={{ 'aria-label': 'primary checkbox' }}
                  />
                </MuiThemeProvider>
              </div>
            </div>
        )

    }
}


function mapStateToProps(state, ownProps) {
  return {
    photoDetails: state.photoDetails
  }
}

export default connect(mapStateToProps, { fetchPhoto })(DashboardView);
//export default DashboardView;
