import logo from './logo.svg';
import './App.css';
import React, { useState } from 'react';

class App extends React.Component {
  constructor() {
    super();
    this.state = {      
      count: 0,
      photo: "" ,
      time: new Date()
    };
  }

  tick = () => {
    this.setState({
      time: new Date()
    });
  }

  componentDidMount() {
    const API_KEY = `${process.env.REACT_APP_UNSPLASH_API_KEY}`;
    fetch("https://api.unsplash.com/collections/764604/photos/?orientation=landscape&client_id=" + API_KEY)
      .then(res => res.json())
      .then(
        (result) => {
          var random_num = Math.floor(Math.random() * result.length());
          this.setState({photo: result[1].urls.regular});
          console.log(result);
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          console.log("error")
        }
      )
      const interval = setInterval(() => {
        this.tick();
      }, 1000);
  }

  render(){
    var greeting = "";
    var ampm = this.state.time.getHours() >= 12 ? 'PM' : 'AM';
    if (this.state.time.getHours() > 8) {
      greeting = "evening";
    }
    else {
      greeting = "morning"
    }

    return (
      <div style={{backgroundImage: 'url(' + this.state.photo + ')', width: '100vw', height: '100vh', backgroundSize: '100% 100%', margin: '0', padding: '0', backgroundRepeat: 'no-repeat'}}>
          <div className="centered" style={{textAlign: 'center'}}>
            <h3 style={{color: 'white'}}>Good {greeting}, Jorden</h3>
            <h1 style={{margin: '0', color: 'white'}}>{this.state.time.getHours()}:{this.state.time.getMinutes()} {ampm}</h1>
          </div>
      </div>
    );
  }
}

export default App;
