import { FETCH_PHOTO, TOGGLE_PHOTO } from '../actions/types';


const initialState = {
    fetchedPhoto: {},
    currentPhoto: {
        urls: {
            regular: ""
        },
        user: {
            first_name: "",
            last_name: "",
            links: {
                html: ""
            }
        },
        links: {
            html: ""
        }
    },
    photoVisible: true,
    loading: true
}

export default function(state = initialState, action) {
    switch(action.type) {
        case FETCH_PHOTO:
            return {
                ...state,
                currentPhoto: state.fetchedPhoto,
                fetchedPhoto: action.payload,
                loading: false
            }
        case TOGGLE_PHOTO:
            return {
                ...state,
                photoVisible: !state.photoVisible
            }
        default:
            return state;
    }
}
