import { CHANGE_VIEW, CHANGE_BACKGROUND_COLOUR, CHANGE_FONT_COLOUR } from './types';

export const changeView = (event) => dispatch => {
    dispatch({
        type: CHANGE_VIEW,
        payload: event.target.value
    });
}

export const changeBackgroundColour = (event) => dispatch => {
    dispatch({
        type: CHANGE_BACKGROUND_COLOUR,
        payload: event.target.value
    });
}

export const changeFontColour = (event) => dispatch => {
    dispatch({
        type: CHANGE_FONT_COLOUR,
        payload: event.target.value
    });
}
