import React from 'react';
import { updateWidget } from '../../actions/widgetActions';
import { connect } from 'react-redux';

class AudioWidget extends React.Component {
    render() {
        return(
            <div style={{padding: '10px', display: "flex", textAlign: 'left', margin: '10px 10px 10px 10px', backgroundColor: '#2D3339', borderRadius: '7px'}}>
                <p style={{color: 'white', fontWeight: 'bolder', marginTop: '3px'}}>Audio</p>
                <select onChange={this.props.updateWidget} ref="addWidget" name="widgets" id="widgets" style={{border: 'none', height: '20px', width: '120px', margin: '3px 0px 0px 0px'}} id={this.props.widget.id}>
                    <option selected disabled>Select Audio</option>
                    <option value="Calm">Calm</option>
                    <option value="Relaxing">Relaxing</option>
                    <option value="Inspirational">Inspirational</option>
                </select>
            </div>
        )
    }
}

function mapStateToProps(state, ownProps) {
    return {
      widgetDetails: state.widgetDetails
    }
}

export default connect(mapStateToProps, { updateWidget })(AudioWidget);
