import '../App.css';
import React from 'react';
import tabsolute from '../images/tabsolutelogo2.png';
import WidgetCustomization from './customization/WidgetCustomization';
import LinkCustomization from './customization/LinkCustomization';

class SettingsBar extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            settingsVisible: false
        }
    }

    toggleSettings = () => {
        console.log('here')
        this.setState({
            settingsVisible: !this.state.settingsVisible
        })
    }

    render() {
        return(
                <div style={{display: 'flex', height: '101vh', margin: '0', padding: '0', position: 'fixed', overflowX: 'hidden', zIndex: '100'}}>
                    <div style={{height: '99vh', width: '320px', backgroundColor: '#202124', borderRadius: '0px 10px 10px 0px', textAlign: 'center', padding: '5px', borderRight: '1px solid #30363D', overflow: 'scroll'}}>
                        <img src={tabsolute} alt="tabsolute_logo" width="85px" height="60px" style={{marginTop: '20px'}} />
                        <h4 style={{color: '#4495F6'}}>TABSOLUTE</h4>
                        <div style={{margin: '20px 0px 20px 0px'}}>
                            <fieldset name="view" style={{border: "none", display: 'flex', color: 'white', margin: 'auto', justifyContent: 'center'}}>
                                <label style={{margin: '0'}}>Dashboard</label>
                                <input type="radio" value="dashboard" name="view" style={{marginRight: '25px'}} onChange={this.props.toggleView} defaultChecked={this.props.viewType === "dashboard"}></input>
                                <label>Link Tiles</label>
                                <input value="tile" type="radio" name="view" onChange={this.props.toggleView} defaultChecked={this.props.viewType === "tile"}></input>
                            </fieldset>
                        </div>
                        <div style={{padding: '10px', display: "flex", textAlign: 'left', margin: '20px 10px 10px 10px', backgroundColor: '#30363D', borderRadius: '7px'}}>
                            <p style={{color: 'white', fontWeight: 'bolder', marginTop: '3px'}}>Add Widget:</p>
                            <form onSubmit={this.props.addNewWidget} style={{marginLeft: '15px'}}>
                                <div style={{display: 'flex'}}>
                                    <select ref="addWidget" name="widgets" id="widgets" style={{border: 'none', height: '20px', width: '120px', margin: '3px 0px 0px 0px'}}>
                                        <option selected disabled>Select Widget</option>
                                        <option value="Greeting">Greeting</option>
                                        <option value="Time">Time</option>
                                        <option value="Quote">Quote</option>
                                        <option value="Weather">Weather</option>
                                    </select>
                                    <button type="submit" style={{backgroundColor: '#0bb508', border: 'none', borderRadius: '10%', fontSize: '15px', margin: '2px 0px 0px 18px', color: 'white', height: '25px', width: '25px', paddingBottom: '2px', cursor: 'pointer'}}>+</button>
                                </div>
                            </form>
                            </div>
                        </div>
                        <div className="clickAway" style={{width: '80vw', height: '100%', cursor: 'pointer'}} onClick={this.toggleSettings}></div>
                </div>
        )
    }
}

export default SettingsBar;
