import '../../App.css';
import React from 'react';
import { deleteWidget, updateWeatherWidget } from '../../actions/widgetActions';
import { connect } from 'react-redux';
import store from '../../store';
import { UPDATE_WIDGET, UPDATE_WEATHER_WIDGET } from '../../actions/types';

class WeatherWidget extends React.Component {
    constructor(){
        super();
        this.state = {
            widget: this.props.widget
        }
    }

    getWeather(event) {
        if (event.key === "Enter") {
            const WEATHER_API_KEY = `${process.env.REACT_APP_WEATHER_API_KEY}`
            fetch('https://api.openweathermap.org/data/2.5/weather?q=' + event.target.value + '&units=metric&appid=' + WEATHER_API_KEY)
            .then(res => res.json())
            .then(
            (result) => {
                const city = event.target.value;
                const temp_min = result.main.temp_min;
                const temp_max = result.main.temp_max;
                const clouds = result.weather[0].description;
                const value = city + ":@:" + temp_min + ":@:" + temp_max + ":@:" + clouds
                //console.log(city, temp_min, temp_max, clouds);
                const payload = {
                    type: UPDATE_WEATHER_WIDGET,
                    payload: value,
                    id: this.state.widget.id
                }
                store.dispatch(payload);
            })
        }
    }

    render() {
        return(
            <div style={{backgroundColor: '#30363D', padding: '10px', margin: '0px 10px 10px 10px', borderRadius: '7px'}}>
                <div style={{textAlign: 'left', margin: '0px 10px 10px 0px'}}>
                    <p style={{float: 'left', color: 'white', fontWeight: 'bolder'}}>{this.props.widget.type}</p>
                    <div style={{float: 'right'}}>
                        <i onClick={this.props.toggleDisplaySettings} id={this.props.widget.id} style={{color: 'white', cursor: 'pointer', margin: '0px 5px 0px 5px'}} class="fas fa-wrench"></i>
                        <i style={{color: 'white', cursor: 'pointer', margin: '0px 0px 0px 5px'}} class="fas fa-trash-alt" onClick={this.props.deleteWidget} id={this.props.widget.id}></i>
                    </div>
                </div>
                <input id={this.props.widget.id} type="text" placeholder="City" defaultValue={this.props.widget.value.split(":@:")[0]} onKeyPress={this.getWeather} style={{marginTop: '10px'}}></input>
                {/*
                {this.props.widget.display.customizationVisible === "true" ? <WidgetCustomization handleFontChange={this.props.handleFontChange} widget={this.props.widget} /> : null}
                */}
            </div>
        )
    }
}

function mapStateToProps(state, ownProps) {
    return {
      widgetDetails: state.widgetDetails
    }
}

export default connect(mapStateToProps, { deleteWidget, updateWeatherWidget })(WeatherWidget);
