import { ADD_WIDGET, DELETE_WIDGET, UPDATE_WIDGET, UPDATE_WEATHER_WIDGET, UPDATE_WIDGET_FONT } from './types';

export const addWidget = (event) => dispatch => {
    dispatch({
        type: ADD_WIDGET,
        payload: event.target[0].value
    });
}

export const deleteWidget = (event) => dispatch => {
    dispatch({
        type: DELETE_WIDGET,
        payload: event
    });
}

export const updateWidget = (event) => dispatch => {
    dispatch({
        type: UPDATE_WIDGET,
        payload: event
    });
}

export function updateWeatherWidget(value){
    return {
        type: UPDATE_WEATHER_WIDGET,
        payload: value
    };
}
