import { FETCH_PHOTO, TOGGLE_PHOTO, SET_DEFAULT_PHOTO } from './types';
import store from '../store';

var last_random_num = -1;

export const fetchPhoto = () => dispatch => {
  const UNSPLASH_API_KEY = `${process.env.REACT_APP_UNSPLASH_API_KEY}`;
  var random_page_num = Math.floor(Math.random() * 100);
  while (random_page_num === last_random_num) {
    random_page_num = Math.floor(Math.random() * 100);
    console.log('recalculating')
  }
  console.log(last_random_num);
  last_random_num = random_page_num;
  console.log(random_page_num);
  console.log(store.getState().photoDetails.lastPhotoIndex)
  fetch("https://api.unsplash.com/collections/19428159/photos/?page= " + random_page_num + "&per_page=1&client_id=" + UNSPLASH_API_KEY)
  .then(res => res.json())
  .then(
    (result) => {
      var photo = result[0];
      dispatch({
        type: FETCH_PHOTO,
        payload: photo,
        lastPhotoIndex: random_page_num
    });
  })
  .catch(function(error) {
    console.log(error);
  });
}

export const togglePhoto = (event) => dispatch => {
  dispatch({
    type: TOGGLE_PHOTO,
    payload: event
  })
}

export const setDefaultPhoto = () => dispatch => {
  dispatch({
    type: SET_DEFAULT_PHOTO
  })
}
