import '../App.css';
import React from 'react';
import tabsolute from '../images/tabsolutelogo2.png';
import WidgetCustomization from './customization/WidgetCustomization';
import LinkCustomization from './customization/LinkCustomization';
import AddWidget from './widgets/AddWidget';
import TabsoluteLogo from './TabsoluteLogo';
import ViewType from './ViewType';
import { connect } from 'react-redux';

class SettingsBar extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            settingsVisible: false
        }
    }

    toggleSettings = () => {
        this.setState({
            settingsVisible: !this.state.settingsVisible
        })
    }

    render() {
        //console.log(JSON.stringify(this.props.widgetDetails.widgets));
        return(
            <div>
                {this.state.settingsVisible ? 
                <div style={{display: 'flex', height: '101vh', margin: '0', padding: '0', position: 'fixed', overflowX: 'hidden', zIndex: '100'}}>
                    <div style={{height: '99vh', width: '320px', backgroundColor: '#202124', borderRadius: '0px 10px 10px 0px', textAlign: 'center', padding: '5px', borderRight: '1px solid #30363D', overflow: 'scroll'}}>
                        <TabsoluteLogo />
                        <ViewType />
                        <AddWidget />
                    </div>
                    {this.props.widgetDetails.widgets.map((widget) => {
                        if (widget.type === "Greeting") {
                            return(
                                <p>Hello!</p>
                            )
                        }
                        else if (widget.type === "Time") {
                            var inputId = "clocktype" + String(widget.id)
                                    return(
                                        <div style={{backgroundColor: '#30363D', padding: '10px', margin: '0px 10px 10px 10px', borderRadius: '7px'}}>
                                            <div style={{textAlign: 'left', margin: '0px 10px 10px 0px'}}>
                                                <p style={{float: 'left', color: 'white', fontWeight: 'bolder'}}>{widget.type}</p>
                                                <div style={{float: 'right'}}>
                                                    <i onClick={this.props.toggleDisplaySettings} id={widget.id} style={{color: 'white', cursor: 'pointer', margin: '0px 5px 0px 5px'}} class="fas fa-wrench"></i>
                                                    <i style={{color: 'white', cursor: 'pointer', margin: '0px 0px 0px 5px'}} class="fas fa-trash-alt" onClick={this.props.handleDelete} id={widget.id}></i>
                                                </div>
                                            </div>
                                            <fieldset id={inputId} style={{border: 'none', display: 'flex', color: 'white', justifyContent: 'center'}}>
                                                <label>12-hour</label>
                                                <input style={{marginTop: '10px'}} type="radio" value="12" onChange={this.props.handleClockChange} id={widget.id} name={inputId} defaultChecked={widget.value === "12"}></input>
                                                <label>24-hour</label>
                                                <input style={{marginTop: '10px'}} type="radio" value="24" onChange={this.props.handleClockChange} id={widget.id} name={inputId} defaultChecked={widget.value !== "12"}></input>
                                            </fieldset>
                                            {widget.display.customizationVisible === "true" ? <WidgetCustomization handleFontChange={this.props.handleFontChange} widget={widget} /> : null}
                                        </div>
                                    )
                        }
                    })}
                    <div className="clickAway" style={{width: '80vw', height: '100%', cursor: 'pointer'}} onClick={this.toggleSettings}></div>
                </div> : <i style={{color: 'white', fontSize: '22px', margin: '7px', cursor: 'pointer'}} className="fas fa-cog" onClick={this.toggleSettings}></i>}
            </div>
        )
    }
}

function mapStateToProps(state, ownProps) {
    return {
      widgetDetails: state.widgetDetails
    }
  }

export default connect(mapStateToProps)(SettingsBar);
