import '../App.css';
import React, { useState, useEffect } from 'react';
import tabsolute from '../images/tabsolutelogo2.png';

class WeatherStatus extends React.Component {
    constructor(props) {
        super(props);
    }

    getWeatherStatus() {
        const WEATHER_API_KEY = `${process.env.REACT_APP_WEATHER_API_KEY}`
        fetch('https://api.openweathermap.org/data/2.5/weather?q=' + this.props.city + '&units=metric&appid=' + WEATHER_API_KEY)
		    .then(res => res.json())
            .then(
            (result) => {
                this.props.handleWeatherChange(result);
		    },
            (error) => {
                console.log("error")
            }
        )
    }

    componentDidMount() {
        this.getWeatherStatus();
    }

    render() {
        return(
            <div style={{float: 'right', textAlign: 'right', margin: '7px'}}>
                <p>City: {this.props.city}</p>
                <p>Temp: {this.props.temp}</p>
                <p>Feels Like: {this.props.feelsLike}</p>
                <p>Clouds: {this.props.clouds}</p>
            </div>
        )
    }
}

export default WeatherStatus;
