import '../App.css';
import React, { useState, useEffect } from 'react';
import tabsolute from '../images/tabsolutelogo2.png';

class WeatherStatus extends React.Component {
    constructor(props) {
        super(props);
        this.state = ({
            temp: "",
            feelsLike: "",
            clouds: ""
        });
    }

    getWeatherStatus() {
        const WEATHER_API_KEY = `${process.env.REACT_APP_WEATHER_API_KEY}`
        fetch('https://api.openweathermap.org/data/2.5/weather?q=' + this.props.city + '&units=metric&appid=' + WEATHER_API_KEY)
		    .then(res => res.json())
            .then(
            (result) => {
                console.log(result);
                this.setState({
                    temp: result.main.temp,
                    feelsLike: result.main.feels_like,
                    clouds: result.weather[0].description
                })
		    },
            (error) => {
                console.log("error")
            }
        )
    }

    componentDidMount() {
        const WEATHER_API_KEY = `${process.env.REACT_APP_WEATHER_API_KEY}`
        fetch('https://api.openweathermap.org/data/2.5/weather?q=' + this.props.city + '&units=metric&appid=' + WEATHER_API_KEY)
		    .then(res => res.json())
            .then(
            (result) => {
                console.log(result);
                this.setState({
                    temp: result.main.temp,
                    feelsLike: result.main.feels_like,
                    clouds: result.weather[0].description
                })
		    },
            (error) => {
                console.log("error")
            }
        )
    }

    render() {
        this.getWeatherStatus();
        return(
            <div style={{float: 'right', textAlign: 'right', margin: '7px'}}>
                <p>Temp: {this.state.temp}</p>
                <p>Feels Like: {this.state.feelsLike}</p>
                <p>Clouds: {this.state.clouds}</p>
            </div>
        )
    }
}

export default WeatherStatus;
