import { CHANGE_VIEW } from './types';

export const changeView = (event) => dispatch => {
    var newView = "";
    if (event.target.value === "tile")
        newView = "dashboard";
    else
        newView = "tile";
    dispatch({
        type: CHANGE_VIEW,
        payload: newView
    });
}
