import '../App.css';
import SettingsBar from './SettingsBar';
import WeatherWidget from './widgets/WeatherWidget';
import Switch from '@material-ui/core/Switch';
import { MuiThemeProvider, createMuiTheme } from '@material-ui/core/styles';
import React from 'react';
import { connect } from 'react-redux';
import { fetchPhoto, fetchTodos } from '../actions/photoActions';
import { loadState, saveState } from '../actions/stateActions';
import store from '../store';

class DashboardView extends React.Component {
    constructor() {
        super();
        this.state = ({
            photoBackground: "true"
        })
    }

    componentDidMount() {
        this.props.fetchPhoto();
        this.props.fetchTodos();
        //console.log(store.getState())
    }

    togglePhotoBackground = () => {
        var toggle = "";
        if (this.state.photoBackground === "true") {
          toggle = "false";
        }
        else {
          toggle = "true";
        }
        this.setState({
          photoBackground: toggle
        }, () => localStorage.setItem("state", JSON.stringify(this.state)))
      }

    render() {
      console.log(this.props.todos)
      console.log(this.props.photo)
        const currentState = store.getState();
        const theme = createMuiTheme({
            palette: {
              primary: {
                main: '#4495F6'
              }
            }
          });

        return (
            <div className="dashboard" style={{backgroundImage: 'url(' + "currentState" + ')', width: '100vw', height: '100vh', backgroundSize: 'cover', margin: '0', padding: '0', backgroundRepeat: 'no-repeat', backgroundColor: '#202124'}}>
                <div style={{position: 'absolute', bottom: '0px'}}>
                    <MuiThemeProvider theme={theme}>
                    <Switch
                        checked={this.state.photoBackground === "true"}
                        onClick={this.togglePhotoBackground}
                        color="primary"
                        name="photoBackground"
                        inputProps={{ 'aria-label': 'primary checkbox' }}
                    />
                    </MuiThemeProvider>
                </div>
                <p>{JSON.stringify(this.props.todosLoading)}</p>
            </div>
        )
    }
}

function mapStateToProps(state) {
    const {photo, photoLoading} = state.photo.photo
    const {todos, todosLoading} = state.photo.todos
}

export default connect(mapStateToProps, { fetchPhoto, fetchTodos })(DashboardView);
