import '../App.css';
import React from 'react';
import { connect } from 'react-redux';

class SearchBar extends React.Component {
    constructor() {
        super();
        this.state = {
            searchText: ''
        }
    }
    handleChange = (event) => {
        this.setState({
            searchText: event.target.value
        })
    }

    render() {
        return (
            <div>
                <input className="search-bar" style={{marginTop: '10px', border: '1px solid ' + this.props.viewDetails.fontColour, width: '475px', fontSize: this.props.widget.display.font + 'px', height: this.props.widget.display.font + 'px', backgroundColor: this.props.viewDetails.backgroundColour, padding: '10px', borderRadius: '100px', color: this.props.viewDetails.fontColour}} type="text" value={this.props.widget.value} onChange={this.handleChange} id={this.props.widget.id} value={this.state.searchText}></input>
                <i style={{color: 'white', margin: '0px 0px 0px -15px'}} className="fas fa-search"></i>
            </div>
        )
    }
}

function mapStateToProps(state, ownProps) {
    return {
      viewDetails: state.viewDetails
    }
  }

export default connect(mapStateToProps)(SearchBar);
