import '../App.css';
import React from 'react';

class WeatherStatus extends React.Component {
    render() {
        return(
            <div style={{textAlign: 'right', margin: '7px', fontSize: this.props.widget.display.font + 'px'}}>
                <p>City: {this.props.widget.value.split(":@:")[0]}</p>
                <p>Temp: {this.props.widget.value.split(":@:")[1]}</p>
                <p>Feels Like: {this.props.widget.value.split(":@:")[2]}</p>
                <p>Clouds: {this.props.widget.value.split(":@:")[3]}</p>
            </div>
        )
    }
}

export default WeatherStatus;
