import './App.css';
import React from 'react';
import SettingsBar from './components/SettingsBar';
import WeatherStatus from './components/WeatherStatus';
import { v4 as uuid } from 'uuid';

class App extends React.Component {
  constructor() {
    super();
    this.state = {      
      photo: "",
      refresh: "false",
      settingsVisible: "false",
      viewType: "dashboard",
      widgets: [
        {
          id: uuid(),
          type: "Greeting",
          value: "",
          display: {
            customizationVisible: "false",
            font: "50",
            location: "center"
          }
        },
        {
          id: uuid(),
          type: "Time",
          value: "12",
          display: {
            customizationVisible: "false",
            font: "50",
            location: "center"
          }
        }
      ],
      tiles: [

      ]
    };
  }

  toggleView = () => {
    var toggle = "";
    if (this.state.viewType == "dashboard") {
      toggle = "tile";
    }
    else {
      toggle = "dashboard";
    }
    this.setState({
      viewType: toggle
    }, () => localStorage.setItem("state", JSON.stringify(this.state)))
  }

  toggleSettings = () => {
    var toggle = "";
    if (this.state.settingsVisible === "true") {
      toggle = "false";
    }
    else {
      toggle = "true";
    }
    this.setState({
      settingsVisible: toggle
    }, () => localStorage.setItem("state", JSON.stringify(this.state)));
  }

  toggleDisplaySettings = (e) => {
      this.setState({
        widgets: this.state.widgets.map((widget) => {
          if (widget.id === e.target.id) {
            if (widget.display.customizationVisible === "false")
              widget.display.customizationVisible = "true";
            else
              widget.display.customizationVisible = "false";
          }
          return widget;
        })
      }, () => localStorage.setItem("state", JSON.stringify(this.state)));
  }

  handleFontChange = (e) => {
    if (e.key === "Enter") {
      this.setState({
        widgets: this.state.widgets.map((widget) => {
          if (widget.id === e.target.id) {
            widget.display.font = e.target.value
          }
          return widget;
        })
      }, () => localStorage.setItem("state", JSON.stringify(this.state)))
    }
  }

  handleClockChange = (e) => {
    this.setState({
      widgets: this.state.widgets.map((widget) => {
        if (widget.id === e.target.id) {
          widget.value = e.target.value
        }
        return widget;
      })
    }, () => localStorage.setItem("state", JSON.stringify(this.state)))
  }

  handleNameChange = (e) => {
    this.setState({
      widgets: this.state.widgets.map((widget) => {
        if (widget.id === e.target.id) {
          widget.value = e.target.value
        }
        return widget;
      })
    }, () => localStorage.setItem("state", JSON.stringify(this.state)))
  }

  handleDelete = (e) => {
    const newWidgetsState = this.state.widgets.filter(widget => e.target.id !== widget.id);
    this.setState({
      widgets: newWidgetsState
    }, () => localStorage.setItem("state", JSON.stringify(this.state)));
  }

  addNewWidget = (e) => {
    e.preventDefault();
    if (e.target[0].value !== "Select Widget") {
      const newWidget = {
        id: uuid(),
        type: e.target[0].value,
        value: "",
        display: {
          customizationVisible: "false",
          font: "20",
          location: "center"
        }
      }

      if (e.target[0].value === "Quote") {
        this.getRandomQuote(newWidget.id);
      }
      else if (e.target[0].value === "Scores") {
        this.getScores(newWidget.id);
      }

      this.setState({
        widgets: [...this.state.widgets, newWidget]
      }, () => localStorage.setItem("state", JSON.stringify(this.state)))
    }
  }

  getScores = (wid) => {
    fetch('https://nhl-score-api.herokuapp.com/api/scores/latest')
      .then(res => res.json())
      .then(
      (result) => {
        console.log(result)
        this.setState({
          widgets: this.state.widgets.map((widget) => {
            if (widget.id === wid) {
              for (var i = 0; i < result.games.length; i++) {
                widget.value += Object.keys(result.games[i].scores);
              }
            }
            return widget;
          })
        }, () => localStorage.setItem("state", JSON.stringify(this.state)))
      }
    )
  }

  getRandomQuote = (wid) => {
    fetch('https://type.fit/api/quotes')
      .then(res => res.json())
      .then(
      (result) => {
        var random_num = Math.floor(Math.random() * result.length);
        this.setState({
          widgets: this.state.widgets.map((widget) => {
            if (widget.id === wid) {
              widget.value = result[random_num].text + ":@:" + result[random_num].author
            }
            return widget;
          })
        }, () => localStorage.setItem("state", JSON.stringify(this.state)))
      }
    )
  }

  getWeatherStatus = (city, wid) => {
    const WEATHER_API_KEY = `${process.env.REACT_APP_WEATHER_API_KEY}`
    fetch('https://api.openweathermap.org/data/2.5/weather?q=' + city + '&units=metric&appid=' + WEATHER_API_KEY)
      .then(res => res.json())
      .then(
      (result) => {
        if (result.hasOwnProperty("main")) {
          this.setState({
            temp_min: result.main.temp_min,
            temp_max: result.main.temp_max,
            clouds: result.weather[0].description
          }, () => localStorage.setItem("state", JSON.stringify(this.state)));

          this.setState({
            widgets: this.state.widgets.map((widget) => {
              if (widget.id === wid) {
                widget.value = city + ":@:" + result.main.temp_min + ":@:" + result.main.temp_max + ":@:" + result.weather[0].description
              }
              return widget;
            })
          }, () => localStorage.setItem("state", JSON.stringify(this.state)))
        }
      },
      (error) => {
          console.log("error")
      }
    )
}

  handleCityChange = (e) => {
    if (e.key === "Enter") {
      this.setState({
        widgets: this.state.widgets.map((widget) => {
          if (widget.id === e.target.id) {
            widget.value = e.target.value
          }
          return widget;
        })
      }, () => localStorage.setItem("state", JSON.stringify(this.state)));
      this.getWeatherStatus(e.target.value, e.target.id);
    }
  }

  tick = () => {
    var current_time = new Date();
    this.setState({
      time: current_time
    });

    if (current_time.getMinutes() % 5 === 0 && current_time.getSeconds() === 0) {
      this.setState({
        refresh: "true"
      }, () => localStorage.setItem("state", JSON.stringify(this.state)));
    }
  }


  componentDidMount() {
    const UNSPLASH_API_KEY = `${process.env.REACT_APP_UNSPLASH_API_KEY}`;
    var getState = JSON.parse(localStorage.getItem("state"));
    if (getState === null) {
      localStorage.setItem("state", JSON.stringify(this.state));
      getState = JSON.parse(localStorage.getItem("state"));
    }
    this.setState(getState);
    this.setState({
      settingsVisible: "false"
    });

    // GET photo
    if (getState.photo === "" || getState.refresh === "true") {
      fetch("https://api.unsplash.com/collections/896608/photos/?orientation=landscape&per_page=100&client_id=" + UNSPLASH_API_KEY)
        .then(res => res.json())
        .then(
          (result) => {
            var random_num = Math.floor(Math.random() * result.length);
            this.setState({
              photo: result[random_num].urls.regular,
              refresh: "false"
            }, () => localStorage.setItem("state", JSON.stringify(this.state)))
          },
          // Note: it's important to handle errors here
          // instead of a catch() block so that we don't swallow
          // exceptions from actual bugs in components.
          (error) => {
            console.log("error")
          }
        )
    }

    // GET weather data
    // this.getWeatherStatus(this.state.city);

    // GET random quote and weather data
    getState.widgets.map((widget) => {
      if (widget.type === "Quote")
        this.getRandomQuote(widget.id);
      else if (widget.type === "Weather")
        this.getWeatherStatus(widget.value, widget.id);
      else if (widget.type === "Scores")
        this.getScores(widget.id);
    })

    setInterval(() => {
      this.tick();
    }, 1000);
  }

  render(){
    const current_time = new Date();
    var greeting = "";
    var ampm = current_time.getHours() >= 12 ? 'PM' : 'AM';
    if (current_time.getHours() >= 21) {
      greeting = "night";
    }
    else if (current_time.getHours() >= 18) {
      greeting = "evening";
    }
    else if (current_time.getHours() >= 12) {
      greeting = "afternoon"
    }
    else {
      greeting = "morning"
    }
    if (this.state.viewType == "dashboard") {
      return (
        <div className="dashboard" style={{backgroundImage: 'url(' + this.state.photo + ')', width: '100vw', height: '100vh', backgroundSize: 'cover', margin: '0', padding: '0', backgroundRepeat: 'no-repeat', backgroundColor: '#202124'}}>
          <SettingsBar settingsVisible={this.state.settingsVisible} toggleSettings={this.toggleSettings} handleNameChange={this.handleNameChange} name={this.state.name} city={this.state.city} handleCityChange={this.handleCityChange} addNewWidget={this.addNewWidget} widgets={this.state.widgets} handleDelete={this.handleDelete} handleClockChange={this.handleClockChange} handleFontChange={this.handleFontChange} toggleDisplaySettings={this.toggleDisplaySettings} toggleView={this.toggleView} />
          <div className="settings">
            <i style={{color: 'white', fontSize: '22px', margin: '7px'}} className="fas fa-cog" onClick={this.toggleSettings}></i>
            <div style={{float:'right'}}>
              {this.state.widgets.map((widget) => {
                if (widget.type === "Weather") 
                  return (<WeatherStatus widget={widget} />)
              })}
            </div>
          </div>
            <div className="centered" style={{textAlign: 'center'}}>
            {this.state.widgets.map((widget) => {
              if (widget.type === "Greeting") {
                if (widget.value !== "") 
                  return (<h3 style={{margin: '0', color: 'white', fontSize: widget.display.font + 'px'}}>Good {greeting}, {widget.value}</h3>)

                else 
                  return (<h3 style={{margin: '0', color: 'white', fontSize: widget.display.font + 'px'}}>Good {greeting}</h3>)

              }
              else if (widget.type === "Quote") {
                return (<div><h4 style={{color: 'white', fontSize: widget.display.font + 'px'}}>{widget.value.split(":@:")[0]}</h4><h4 style={{color: 'white', fontSize: widget.display.font + 'px'}}>- {widget.value.split(":@:")[1]}</h4></div>)
              }
              else if (widget.type === "Scores") {
                return (<div><h4 style={{color: 'white', fontSize: widget.display.font + 'px'}}>{widget.value}</h4></div>)
              }
              else if (widget.type === "Links") {
                return (<div style={{backgroundColor: '#202124', width: '25%', textAlign: 'center', margin: 'auto', borderRadius: '10px'}}><i className="fas fa-cog" style={{color: 'white', marginTop: '15px', fontSize: '20px'}}></i><h4 style={{padding: '10px', color: 'white', fontSize: widget.display.font + 'px'}}>{widget.value}</h4></div>)
              }
              else if (widget.type === "Time") {
                if (widget.value === "12")
                  return (<h1 style={{margin: '0', color: 'white', fontSize: widget.display.font + 'px'}}>{current_time.toLocaleTimeString().split(":")[0]}:{current_time.toLocaleTimeString().split(":")[1]} {ampm}</h1>)
                else
                  return (<h1 style={{margin: '0', color: 'white', fontSize: widget.display.font + 'px'}}>{current_time.toTimeString().split(":")[0]}:{current_time.toTimeString().split(":")[1]}</h1>)
              }
              }
            )}
          </div>
        </div>
      )
    }
      else {
        return(
          <div className="dashboard" style={{backgroundImage: 'url(' + this.state.photo + ')', width: '100vw', height: '100vh', backgroundSize: 'cover', margin: '0', padding: '0', backgroundRepeat: 'no-repeat', backgroundColor: '#202124'}}>
            <SettingsBar settingsVisible={this.state.settingsVisible} toggleSettings={this.toggleSettings} handleNameChange={this.handleNameChange} handleCityChange={this.handleCityChange} addNewWidget={this.addNewWidget} widgets={this.state.widgets} handleDelete={this.handleDelete} handleClockChange={this.handleClockChange} handleFontChange={this.handleFontChange} toggleDisplaySettings={this.toggleDisplaySettings} toggleView={this.toggleView} />
            <div className="settings">
              <i style={{color: 'white', fontSize: '22px', margin: '7px'}} className="fas fa-cog" onClick={this.toggleSettings}></i>
            </div>
              <div className="centered" style={{textAlign: 'center'}}>
                {this.state.tiles.map((tile) => {
                })}
              </div>
          </div>
        )
      }
    }
  }

export default App;
