import '../App.css';
import React from 'react';

class Time extends React.Component {
    componentDidMount() {
        setInterval(() => {
            this.tick();
          }, 1000);
    }

    render() {
        const current_time = new Date();
        var ampm = current_time.getHours() >= 12 ? 'PM' : 'AM';
        if (this.props.widget.value === "12")
            return (<h1 style={{margin: '0', color: 'white', fontSize: this.props.widget.display.font + 'px'}}>{current_time.toLocaleTimeString().split(":")[0]}:{current_time.toLocaleTimeString().split(":")[1]} {ampm}</h1>)
        else
            return (<h1 style={{margin: '0', color: 'white', fontSize: this.props.widget.display.font + 'px'}}>{current_time.toTimeString().split(":")[0]}:{current_time.toTimeString().split(":")[1]}</h1>)
    }
}

export default Time;
