import '../App.css';
import React from 'react';

class SearchBar extends React.Component {
    render() {
        return (
            <div>
                <input style={{marginTop: '10px', border: 'none', width: '300px', fontSize: this.props.widget.fontSize}} type="text" value={this.props.widget.value} onChange={this.props.updateWidget} id={this.props.widget.id} placeholder="Name"></input>
            </div>
        )
    }
}

export default SearchBar;
