import '../App.css';
import SettingsBar from './SettingsBar';
import Greeting from './Greeting';
import Time from './Time';
import Quote from './Quote';
import Weather from './Weather';
import Switch from '@material-ui/core/Switch';
import { MuiThemeProvider, createMuiTheme } from '@material-ui/core/styles';
import React from 'react';
import { connect } from 'react-redux';
import { fetchPhoto, togglePhoto } from '../actions/photoActions';
import store from '../store';

class DashboardView extends React.Component {
  componentDidMount() {
    this.props.fetchPhoto();
    /*
    if (Object.keys(store.getState().photoDetails.photo).length === 0) {
      this.props.fetchPhoto();
    }
    */
  }

  render() {
    const theme = createMuiTheme({
      palette: {
        primary: {
          main: '#4495F6'
        }
      }
    });
    var photoURL = ""
    if (!this.props.photoDetails.loading) {
      photoURL = this.props.photoDetails.photo.urls.regular;
      if (!this.props.photoDetails.photoVisible)
        photoURL = "";
    }

    return (
        <div className="dashboard" style={{backgroundImage: 'url(' + photoURL + ')', width: '100vw', height: '100vh', backgroundSize: 'cover', margin: '0', padding: '0', backgroundRepeat: 'no-repeat', backgroundColor: '#202124'}}>
          <div>
            <div style={{float: 'left'}}>
              <SettingsBar />
            </div>
            <div style={{float:'right'}}>
              {this.props.widgetDetails.widgets.map((widget) => {
                if (widget.type === "Weather") 
                  return (<Weather widget={widget} />)
              })}
            </div>
          </div>
          <div className="centered" style={{textAlign: 'center'}}>
            {this.props.widgetDetails.widgets.map((widget) => {
              if (widget.type === "Greeting") {
                return(<Greeting widget={widget} />)
              }
              else if (widget.type === "Time") {
                return(<Time widget={widget} />)
              }
              else if (widget.type === "Quote") {
                return(<Quote widget={widget} />)
              }
            })}
          </div>
          <div style={{position: 'absolute', bottom: '0px'}}>
            <MuiThemeProvider theme={theme}>
              <Switch
                checked={this.props.photoDetails.photoVisible}
                onClick={this.props.togglePhoto}
                color="primary"
                name="photoBackground"
                inputProps={{ 'aria-label': 'primary checkbox' }}
              />
            </MuiThemeProvider>
          </div>
          {this.props.photoDetails.photoVisible ? <div style={{position: 'absolute', bottom: '5px', right: '5px'}}>
            <p><a href={this.props.photoDetails.photo.links.html + "https://unsplash.com/?utm_source=tabsolute&utm_medium=referral"}>Photo</a> by <a href={this.props.photoDetails.photo.user.links.html + "/?utm_source=tabsolute&utm_medium=referral"}>{this.props.photoDetails.photo.user.first_name} {this.props.photoDetails.photo.user.last_name}</a> on <a href="https://unsplash.com/?utm_source=tabsolute&utm_medium=referral">Unsplash</a></p>
            </div> : null}
        </div>
    )
  }
}


function mapStateToProps(state, ownProps) {
  return {
    photoDetails: state.photoDetails,
    widgetDetails: state.widgetDetails
  }
}

export default connect(mapStateToProps, { fetchPhoto, togglePhoto })(DashboardView);
