import '../App.css';
import React, { useState, useEffect } from 'react';
import tabsolute from '../images/tabsolutelogo2.png';

class SettingsBar extends React.Component {
    constructor(props) {
        super(props);
    }

    render() {
        return(
            <div>
                {this.props.settingsVisible ? 
                    <div style={{display: 'flex', height: '100vh', margin: '0', padding: '0', position: 'fixed', overflowX: 'hidden', zIndex: '100'}}>
                        <div style={{height: '100%', width: '20vw',backgroundColor: 'white', borderRadius: '0px 10px 10px 0px', textAlign: 'center'}}>
                            <img src={tabsolute} width="85px" height="60px" style={{marginTop: '20px'}} />
                            <h4 style={{color: '#4495F6'}}>TABSOLUTE</h4>
                            <div style={{display: "flex", textAlign: 'left', margin: '20px 10px 10px 10px', width: '100%'}}>
                                <p style={{color: 'black'}}>Name:</p>
                                <input type="text" defaultValue={this.props.name} onChange={this.props.handleNameChange}></input>
                            </div>
                            <div style={{display: "flex", textAlign: 'left', margin: '20px 10px 10px 10px', width: '100%'}}>
                                <p style={{color: 'black'}}>Weather:</p>
                                <input type="text" placeholder="City" defaultValue={this.props.city} onKeyPress={this.props.handleCityChange}></input>
                            </div>
                        </div> 
                        <div className="clickAway" style={{width: '80vw', height: '100%'}} onClick={this.props.toggleSettings}></div>
                    </div>
                    : null }
            </div>
        )
    }
}

export default SettingsBar;
