import '../App.css';
import SettingsBar from './SettingsBar';
import WeatherWidget from './widgets/WeatherWidget';
import Switch from '@material-ui/core/Switch';
import { MuiThemeProvider, createMuiTheme } from '@material-ui/core/styles';
import React from 'react';
import { connect } from 'react-redux';
import { fetchPhoto } from '../actions/photoActions';

class DashboardView extends React.Component {
    componentDidMount() {
        this.props.fetchPhoto();
    }

    render() {
        var photoURL;
    if (this.state.photoBackground === "true") {
      photoURL = this.state.photo;
    }
    else {
      photoURL = ""
    }
    const current_time = new Date();
    var greeting = "";
    var ampm = current_time.getHours() >= 12 ? 'PM' : 'AM';
    if (current_time.getHours() >= 21) {
      greeting = "night";
    }
    else if (current_time.getHours() >= 18) {
      greeting = "evening";
    }
    else if (current_time.getHours() >= 12) {
      greeting = "afternoon"
    }
    else {
      greeting = "morning"
    }

    const theme = createMuiTheme({
      palette: {
        primary: {
          main: '#4495F6'
        }
      }
    });
        return (
            <div className="dashboard" style={{backgroundImage: 'url(' + photoURL + ')', width: '100vw', height: '100vh', backgroundSize: 'cover', margin: '0', padding: '0', backgroundRepeat: 'no-repeat', backgroundColor: '#202124'}}>
            <SettingsBar settingsVisible={this.state.settingsVisible} toggleSettings={this.toggleSettings} handleNameChange={this.handleNameChange} handleCityChange={this.handleCityChange} addNewWidget={this.addNewWidget} widgets={this.state.widgets} handleDelete={this.handleDelete} handleClockChange={this.handleClockChange} handleFontChange={this.handleFontChange} toggleDisplaySettings={this.toggleDisplaySettings} toggleView={this.toggleView} viewType={this.state.viewType} linkWidget={this.state.linkWidget} tiles={this.state.tiles} addNewLinkTile={this.addNewLinkTile} handleTileChange={this.handleTileChange} handleTileDelete={this.handleTileDelete} />
            <div className="settings">
                <i style={{color: 'white', fontSize: '22px', margin: '7px'}} className="fas fa-cog" onClick={this.toggleSettings}></i>
                <div style={{float:'right'}}>
                {this.state.widgets.map((widget) => {
                    if (widget.type === "Weather") 
                    return (<WeatherWidget widget={widget} />)
                })}
                </div>
            </div>
                <div className="centered" style={{textAlign: 'center'}}>
                {this.state.widgets.map((widget) => {
                if (widget.type === "Greeting") {
                    if (widget.value !== "") 
                    return (<h3 style={{margin: '0', color: 'white', fontSize: widget.display.font + 'px'}}>Good {greeting}, {widget.value}</h3>)

                    else 
                    return (<h3 style={{margin: '0', color: 'white', fontSize: widget.display.font + 'px'}}>Good {greeting}</h3>)

                }
                else if (widget.type === "Quote") {
                    return (<div><h4 style={{color: 'white', fontSize: widget.display.font + 'px'}}>{widget.value.split(":@:")[0]}</h4><h4 style={{color: 'white', fontSize: widget.display.font + 'px'}}>- {widget.value.split(":@:")[1]}</h4></div>)
                }
                else if (widget.type === "Scores") {
                    return (<div><h4 style={{color: 'white', fontSize: widget.display.font + 'px'}}>{widget.value}</h4></div>)
                }
                else if (widget.type === "Links") {
                    return (<div style={{backgroundColor: '#202124', width: '25%', textAlign: 'center', margin: 'auto', borderRadius: '10px'}}><i className="fas fa-cog" style={{color: 'white', marginTop: '15px', fontSize: '20px'}}></i><h4 style={{padding: '10px', color: 'white', fontSize: widget.display.font + 'px'}}>{widget.value}</h4></div>)
                }
                else if (widget.type === "Time") {
                    if (widget.value === "12")
                    return (<h1 style={{margin: '0', color: 'white', fontSize: widget.display.font + 'px'}}>{current_time.toLocaleTimeString().split(":")[0]}:{current_time.toLocaleTimeString().split(":")[1]} {ampm}</h1>)
                    else
                    return (<h1 style={{margin: '0', color: 'white', fontSize: widget.display.font + 'px'}}>{current_time.toTimeString().split(":")[0]}:{current_time.toTimeString().split(":")[1]}</h1>)
                }
                }
                )}
            </div>
            <div style={{position: 'absolute', bottom: '0px'}}>
                <MuiThemeProvider theme={theme}>
                <Switch
                    checked={this.state.photoBackground === "true"}
                    onClick={this.togglePhotoBackground}
                    color="primary"
                    name="photoBackground"
                    inputProps={{ 'aria-label': 'primary checkbox' }}
                />
                </MuiThemeProvider>
            </div>
            {this.state.photoBackground === "true" ? <div style={{position: 'absolute', bottom: '5px', right: '5px'}}>
                <p><a href={this.state.photoDetails.photoLink + "https://unsplash.com/?utm_source=tabsolute&utm_medium=referral"}>Photo</a> by <a href={this.state.photoDetails.profileLink + "/?utm_source=tabsolute&utm_medium=referral"}>{this.state.photoDetails.artist}</a> on <a href="https://unsplash.com/?utm_source=tabsolute&utm_medium=referral">Unsplash</a></p>
            </div> : null}

            </div>
        )
    }
}

export default DashboardView;
